dict.string:
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function run(argv) {
  const modeRemove = envVar("action") === "unignore" // Either "ignore" to add to list or "unignore" to remove from it
  const vm = argv[0]

  // Read plist for ignored VMs
  const task = $.NSTask.alloc.init
  const stdout = $.NSPipe.pipe

  task.executableURL = $.NSURL.fileURLWithPath("/usr/libexec/PlistBuddy")
  task.arguments = ["-c", "Print :ignored_vms", "prefs.plist"]
  task.standardOutput = stdout
  task.launchAndReturnError(false)

  const dataOut = stdout.fileHandleForReading.readDataToEndOfFileAndReturnError(false)
  const stringOut = $.NSString.alloc.initWithDataEncoding(dataOut, $.NSUTF8StringEncoding).js

  task.waitUntilExit

  // Set ignored
  const origIgnored = stringOut.split("\n").filter(entry => entry) // Split and remove empty entries
  const ignored = modeRemove ?
    origIgnored.filter(entry => entry !== vm) :
    origIgnored.concat(vm)

  Application("com.runningwithcrayons.Alfred").setConfiguration(
    "ignored_vms",
    { toValue: ignored.join("\n"), inWorkflow: envVar("alfred_workflow_bundleid") }
  )
}
