dict.string:
// Helpers
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

// Main
function run(argv) {
  const vm = argv[0]
  const modeRemove = envVar("action") === "unignore" // Either "ignore" to add to list or "unignore" to remove from it

  // Set ignored
  const origIgnored = envVar("ignored_vms")?.split("\n")?.filter(entry => entry) || [] // Split and remove empty entries
  const ignored = modeRemove ?
    origIgnored.filter(entry => entry !== vm) :
    origIgnored.concat(vm)

  Application("com.runningwithcrayons.Alfred").setConfiguration(
    "ignored_vms",
    { toValue: ignored.join("\n"), inWorkflow: envVar("alfred_workflow_bundleid") }
  )
}
